<template>
  <div class="user" @click="viewProfile()">
    <div class="user__photo">
      <img :src="cPhoto" :alt="`Foto de ${user.name}`">
    </div>
    <div class="user__info">
      <div class="user__info-user ellipsis">
        {{ user.user }}
      </div>
      <div class="user__info-name ellipsis">
        {{ user.name }}
      </div>
      <div class="user__info-slot ellipsis">
        <slot></slot>
      </div>
    </div>
  </div>
</template>

<script>
// El User tendrá simplemente la foto, nombre, y un slot donde meter la info extra
// En el caso del perfil el slot serán los seguidores/seguidos
// En el caso de un Poe en el timeline, será info de cabecera del Poe
// En el caso de Alta nuevo Poe, será sin slot y como mucho, si da mapa
// se pondrá la info del mapa.
// En el caso de los perfiles trends de la landing, serán los seguidores que tiene
const defaultPhotos = [
  'data:image/png;base64,',
  'data:image/png;base64,',
  'data:image/png;base64,',
  'data:image/png;base64,',
  'data:image/png;base64,'
]
export default {
  props: ['user', 'clickToProfile'],
  name: 'user-component',
  data () {
    return {
      cPhoto: null
    }
  },
  beforeMount() {
    if (!this.user.photo) {
      this.cPhoto = this.getRandomPhoto()
    } else {
      this.cPhoto = this.user.photo
    }
  },
  methods: {
    getRandomPhoto() {
      return defaultPhotos[Math.floor(Math.random() * (4 - 0)) + 0]
    },
    viewProfile() {
      if (!this.clickToProfile) return
      // TODO
      alert(`ver perfil de ${this.user.user}`)
    }
  }
}
</script>

